import React from 'react';
import logo from  './icon.jpg';
import './App.css';

class App extends React.Component
{
  constructor(props){
    super(props);
    this.state = {
      newItem:"",
      list: []
    }
  }

  addItem(todo){
    if(todo.value !== ""){
      const newtodo ={
        id: Date.now(), value: todo, isDone: false
      };
      const list = [...this.state.list];
      list.push(newtodo);

      this.setState({
        list,
        newtodo: ""
      });
    }
  }

  deleteItem(id)
  {
    const list = [...this.state.list];
    const updatedlist = list.filter(item => item.id !== id);
    this.setState({
      list: updatedlist })
  }

  updateInput(input){
    this.setState({newtodo:input});
  }

  render(){
    return(
      <div>
        <img src={logo} width="100" height="100" className="logo"/>
        <h1 className="app-title">To do APP</h1>
        <div className="container">
          <h3>ADD AN ITEM</h3>
          <input type="text" className="input-text" placeholder="Write a ToDo" required value={this.state.newtodo} onChange={e => this.updateInput(e.target.value)}/>

          <button className="add-btn" onClick={()=>this.addItem(this.state.newtodo)}  >ADD</button>

          <div className="add-list">
            <ol>
              {this.state.list.map(item => {
                return(
                  <li key={item.id}>
                      <input type="checkbox" name="isDone" checked={item.isDone} onChange = {() => {}}/>
                      {item.value}
                      <button className="btn-del" onClick={this.deleteItem(item.id)}></button>
                  </li>
                );
              })}
              <li>
                <input type="checkbox" className="chkbox" id="cb1" />
                Complete Namrata's Order
                <button className="btn-del">DELETE</button>
              </li>
            </ol>
          </div>
        </div>

      </div>
    );
  }
}
export default App;
